import React, { useState, useEffect } from 'react'

const News = () => {
    const [News, setNews] = useState([])

    useEffect(() => {
        getnews();
    }, [])

    const getnews = async () => {
        let response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3ba5e7b3ba4e44c7b922ea03462be5d6`)
        let data = await response.json();
        setNews(data.articles)
    }

    return (
        <div className='container'>
            <div className='row'>
                {News.map((value, index) => {
                    const description = value.description.split(' ').slice(0, 15).join(' ');
                    const title = value.title.split(' ').slice(0, 5).join(' ');

                        return (
                            <figure class="snip1493">
                            <div class="image"><img src={value.urlToImage} alt="ls-sample1" /></div>
                            <figcaption>
                              <div class="date"><span class="day">28</span><span class="month">Oct</span></div>
                              <h3>{title}</h3>
                              <p>

                              {description}
                              </p>
                              <footer>
                                <div class="views"><i class="ion-eye"></i>2,907</div>
                                <div class="love"><i class="ion-heart"></i>623</div>
                                <div class="comments"><i class="ion-chatboxes"></i>23</div>
                              </footer>
                            </figcaption>
                            <a href="#"></a>
                          </figure>
                        )

                })}
            </div>
        </div>
    )
}

export default News
