import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
const Profile = () => {
    const navigate = useNavigate();
    const [data, setdata] = useState([]);


    useEffect(() => {
        getroom();
        getrating();
        // getWithPromiseAll();
    }, [])
    // getWithPromiseAll();

    const getroom = async () => {
        let response = await fetch('http://localhost:8080/api/product');
        let res = await response.json();
        setdata(res.message);
        console.log(res)
    }


    const booknow = (id) => {
        navigate('/payment', { state: { id: id } })
    }


    return (
        <div className='container'>
            <div className='row'>
                {data.map( (value, index) => {
                    const description = value.description.split(' ').slice(0, 10).join(' ');

                        return (
                            <div className='col-md-5'>
                                <div className="card2 mb-3 mt-5" style={{ maxWidth: "740px" }}>
                                    <div className="row no-gutters">
                                        <div className="col-md-12">
                                            <img src={value.image} className="card-img" alt="..." />
                                        </div>
                                        <div className="col-md-12">
                                            <div className="card-body">
                                                <h5 className="card-title">{value.title}</h5>
                                                <p className="card-text">{description}... <a href="" onClick={() => booknow(value._id)} >Read More</a></p>

                                                <p className="card-text" style={{ fontWeight: "bold" }}> &nbsp; &nbsp; &nbsp; &nbsp; RS: {value.price} </p>


                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )

                 })}
            </div>
        </div>
    )
}

export default Profile
