import React, { useState, useEffect } from 'react'

const News = () => {
    const [News, setNews] = useState([])

    useEffect(() => {
        getnews();
    }, [])

    const getnews = async () => {
        let response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3ba5e7b3ba4e44c7b922ea03462be5d6`)
        let data = await response.json();
        setNews(data.articles)
    }

    return (
        <div className='container'>
            <div className='row'>
                {News.map((value, index) => {
                    const description = value.description.split(' ').slice(0, 15).join(' ');

                        return (
                            <div className='col-md-5'>
                                <div className="card2 mb-3 mt-5" style={{ maxWidth: "740px" }}>
                                    <div className="row no-gutters">
                                        <div className="col-md-12">
                                            <img src={value.urlToImage} className="card-img" alt="Not found" />
                                        </div>
                                        <div className="col-md-12">
                                            <div className="card-body">
                                                <h5 className="card-title">{value.title}</h5>
                                                <p className="card-text">{description}... <a href={value.url}>Read More</a></p>

                                                <p className="card-text" style={{ fontWeight: "bold" }}> &nbsp; &nbsp; &nbsp; &nbsp; author: {value.author} </p>
                                               <p className="card-text"> Source : {value.source.name}</p><br /><br />

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )

                })}
            </div>
        </div>
    )
}

export default News
