import React, { useState } from 'react'
import Select from 'react-select'

import {
    ref,
    uploadBytes,
    getDownloadURL
  } from "firebase/storage";
  import { storage } from "../config";
  import { v4 } from "uuid";

const Room = () => {
    const [imageUpload, uploadimg] = useState(null);
    const [roominfo, Setroominfo] = useState([])

    const myData = [
        { label: 'Books', value: 1 },
        { label: 'Movies, Music & Games', value: 2 },
        { label: 'Electronics & Computers', value: 3 },
        { label: 'Home, Garden & Tools', value: 4 },
        { label: 'Health & Beauty', value: 5 },
        { label: 'Toys, Kids & Baby', value: 6 },
        { label: 'Clothing & Jewelry', value: 7 },
        { label: 'Sports & Outdoors', value: 8 },
        { label: 'Automotive & Industrial', value: 9 }
    ];

    const facility = (res) => {
        Setroominfo(res)
    }

    const savehandler = async (res) => {

        const imageRef = ref(storage, `Booking/${imageUpload.name + v4()}`);
        uploadBytes(imageRef, imageUpload).then((snapshot) => {
            getDownloadURL(snapshot.ref).then(async (url) => {

                let response = await fetch(`http://localhost:8080/api/user`, {
                    method: "POST",
                    headers: {
                        'Access-Control-Allow-Origin': '*',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ name: name, email: email, password: password, mobile: mobile, profile: url })
                });
                let res = await response.json();
                console.log(res)

            });
        });

    }
    return (
        <div className='card'>
            <h3>Create a New Room</h3>
            <div className='form-group'>
                <label>Enter Title</label>
                <input className='form-control' type="text" id="name" placeholder="Enter Title" />
            </div>

            <div className='form-group'>
                <label>Enter description</label>
                <textarea className='form-control' id="description" placeholder="Enter Description..." ></textarea>
            </div>

            <div className='form-group'>
                <label>Room Type</label>
                <select className='form-control' id='type'>
                    <option value="Single Bed">Single Bed</option>
                    <option value="Double Bed">Double Bed</option>

                </select>
            </div>

            <div className='form-group'>
                <label>Price Per Night</label>
                <input className='form-control' type="text" id="price" placeholder="Enter Price" />
            </div>
            <label>Room Facility</label>
            <Select
                options={myData}
                value={roominfo}
                isMulti={true}
                placeholder="Select Facility"
                onChange={facility}
            />

            <div className='form-group'>
                <label>Upload Room Image</label>
                <input className='form-control' type="file" onChange={(event) => uploadimg(event.target.files[0])} />
            </div>


            <div className='form-group'>
                <button onClick={savehandler} className='btn btn-block btn-success'>Add Room</button>
            </div>

        </div>
    )
}

export default Room
