import React,{useState,useEffect} from 'react'

const Profile = () => {
  const [data,setdata] = useState([]);

    useEffect(() => {
        getroom();
    }, [])

    const getroom = async () => {
        let response = await fetch('http://localhost:8080/api/room');
        let res = await response.json();
        setdata(res.message);
        console.log(res)
    }
  return (
      <div className='container'>
        {data && data.map((value,index)=>{
            let obj = JSON.parse(value.facility);
            const description = value.description.split(' ').slice(0, 15).join(' ');
            return (
                <div className="card2 mb-3 mt-5" style={{ maxWidth: "740px" }}>
                    <div className="row no-gutters">
                        <div className="col-md-5">
                            <img src={value.image} className="card-img" alt="..." />
                        </div>
                        <div className="col-md-7">
                            <div className="card-body">
                                <h5 className="card-title">{value.title}</h5>
                                <p className="card-text">{description}...</p>
                                <a href='#' >Read More</a>
                                <p className="card-text"><small className="text-muted">Room Facility</small></p>
                                {value.facility.map((fact)=>{
                                    return <li>{fact.label}</li>
                                })}
                            </div>
                        </div>
                    </div>
                </div>
            )
          })}
      </div>
  )
}

export default Profile
