import React, { useState, useEffect } from 'react'
import ReactPaginate from 'react-paginate';
import InfiniteScroll from "react-infinite-scroll-component";

const News = () => {
    const [currentPage, setCurrentPage] = useState(0);
    const [News, setNews] = useState([])
    const [text, setText] = useState("");
    const [date, setdate] = useState();
    const [cate,setcat] = useState('business');


    const month = ['xyz', 'JAN', 'FEB', 'MAR', 'APR', 'MAY', 'JUNE', 'JULY', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];

    useEffect(() => {
        getnews();
    }, [cate])

    let currentPageData = [];
    const PER_PAGE = 50;
    let pageCount = 0;
    const offset = currentPage * PER_PAGE;
    function handlePageClick({ selected: selectedPage }) {
        setCurrentPage(selectedPage);
    }
    pageCount = Math.ceil(News.length / PER_PAGE)

    const getnews = async () => {
        let response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&category=${cate}&apiKey=3ba5e7b3ba4e44c7b922ea03462be5d6&pageSize=20&page=1`)
        let data = await response.json();
        setNews(data.articles)
        console.log(data)
    }

    const fetchMoreData = ()=>{
        console.log('hello')
    }


    return (
        <div className='container marginTop'>
            <div className='text-center mt-2 mb-2'>
                <input className='form-control' style={{ width: "300px" }} type="text" onChange={(e) => setText(e.target.value)} placeholder="Search..." />
                <div className='form-group mt-2'>
                    <label style={{ float: "left" }}>Select Category</label><br />
                   <select onChange={(e)=>setcat(e.target.value)} className="form-control" style={{ width: "300px" }}>
                    <option value="business">Business</option>
                    <option value="entertainment">Entertainment</option>
                    <option value="health">Health</option>
                    <option value="science">Science</option>
                    <option value="sports">Sports</option>
                    <option value="technology">Technology</option>

                   </select>
                </div>
            </div>
            <InfiniteScroll
                dataLength={News.length}
                next={fetchMoreData}
                hasMore={true}
                loader={<h4>Loading...</h4>}
            >
            <div className='row'>

                {currentPageData = News
                    .slice(offset, offset + PER_PAGE).filter((value) => {
                        if (text == "") {
                            return value
                        } else if (value.title.toLowerCase().includes(text.toLowerCase())) {
                            return value;
                        }
                    }).map((value, index) => {
                        const date = value.publishedAt.split('T');
                        const new_date = date[0];
                        const ekour = new_date.split('-');
                        let curr_month = month[parseInt(ekour[1], 10)];
                        // let description = value.description.split(' ').slice(0, 15).join(' ');
                        const title = value.title.split(' ').slice(0, 5).join(' ');

                        return (
                            <figure className="snip1493">
                                <div className="image"><img src={value.urlToImage} alt="ls-sample1" /></div>
                                <figcaption>
                                    <div className="date"><span className="day">{ekour[2]}</span><span className="month">{curr_month}</span></div>
                                    <h3>{title}</h3>
                                    <p>
                                        {value.description}
                                    </p>
                                    <footer>
                                        <div className="views"><i className="ion-eye"></i>2,907</div>
                                        <div className="love"><i className="ion-heart"></i>623</div>
                                        <div className="comments"><i className="ion-chatboxes"></i>23</div>
                                    </footer>
                                </figcaption>
                                <a href={value.url} target="_blank"><span className='source_opt'>{value.source.name}</span></a>
                            </figure>
                        )

                    })}

            </div>
            </InfiniteScroll>

            <ReactPaginate
                previousLabel={"← Previous"}
                nextLabel={"Next →"}
                pageCount={pageCount}
                onPageChange={handlePageClick}
                containerClassName={"pagination mt-2 mb-5"}
                subContainerClassName={"pages pagination"}
                activeClassName={"active"}
            />
        </div>
    )
}

export default News
