import React, { useState, useEffect } from 'react'

const News = () => {
    const [News, setNews] = useState([])
    const month = ['xyz','JAN','FEB','MAR','APR','MAY','JUNE','JULY','AUG','SEP','OCT','NOV','DEC'];

    useEffect(() => {
        getnews();
    }, [])

    const getnews = async () => {
        let response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3ba5e7b3ba4e44c7b922ea03462be5d6`)
        let data = await response.json();
        setNews(data.articles)
        console.log(data)
    }

    return (
        <div className='container marginTop'>
            <div className='row'>
                {News.map((value, index) => {
                    const date = value.publishedAt.split('T');
                    const new_date = date[0];
                    const ekour = new_date.split('-');
                    let curr_month = month[parseInt(ekour[1],10)];
                    // let description = value.description.split(' ').slice(0, 15).join(' ');
                    const title = value.title.split(' ').slice(0, 5).join(' ');

                        return (
                            <figure className="snip1493">
                            <div className="image"><img src={value.urlToImage} alt="ls-sample1" /></div>
                            <figcaption>
                              <div className="date"><span className="day">{ekour[2]}</span><span className="month">{curr_month}</span></div>
                              <h3>{title}</h3>
                              <p>
                              {value.description}<a href={value.url} > Read More...</a>
                              </p>
                              <footer>
                                <div className="views"><i className="ion-eye"></i>2,907</div>
                                <div className="love"><i className="ion-heart"></i>623</div>
                                <div className="comments"><i className="ion-chatboxes"></i>23</div>
                              </footer>
                            </figcaption>
                            <a href=''><span className='source_opt'>{value.source.name}</span></a>
                          </figure>
                        )

                })}
            </div>
        </div>
    )
}

export default News
