import React, {useEffect,useState} from 'react'

const Test = () => {

  const initial = [
    {
        Appointment: 12345,
        charge: 26,
        date: '2022-07-12'
    },

    {
      Appointment: 12346,
      charge: 20,
      date: '2022-08-12'
    },

    {
      Appointment: 12347,
      charge: 30,
      date: '2022-06-05'
    },
    {
      Appointment: 12348,
      charge: 22,
      date: '2022-08-09'
    },
    {
      Appointment: 12349,
      charge: 34,
      date: '2022-08-11'
    }
];

const [payment,Setpayment] = useState(initial)
const [price,setPrice] = useState(null);

useEffect(()=>{

 getdata()


},[price])

const getdata = ()=>{
  if(price == 'price_low_to_high'){
    // console.log(payment)
   payment.sort((a, b) =>{ return a.charge - b.charge}) 
    Setpayment([...payment]);

   }else if(price == 'price_high_to_low'){
    payment.sort((a, b) =>{return b.charge - a.charge})
   Setpayment([...payment]);

   }else if(price == 'date_low_to_high'){
    Setpayment(payment.sort((a, b) => a.date - b.date));

   }else if(price == 'date_high_to_low'){
    Setpayment(payment.sort((a, b) => b.date - a.date));
   }
}





 const updatehandle = (e)=>{
   setPrice(e.target.value)
 }

  return (
   <div className='container'>
    <select onChange={updatehandle} className="form-control mt-5" style={{width:"300px"}}>
      <option value="">Sort By</option>
      <option value="price_low_to_high">Price low to high</option>
      <option value="price_high_to_low">Price high to low</option>
      <option value="date_low_to_high">Date low to high</option>
      <option value="date_high_to_low">Date high to low</option>

    </select>
      <ul className="mt-5" style={{listStyle:"none"}}>
        {payment.map((value,index)=>{

          return(
            <li key={index} style={{padding:"15px",borderBottom:"1px solid lightgray"}}>Appointment: {value.Appointment}<br />
            Charge: &nbsp;&nbsp;&nbsp; ${value.charge}<br />
            {value.date}
            </li>
          )


        })

      }

      </ul>
   </div>

  )
}

export default Test
