import { useLocation, useNavigate } from 'react-router-dom';

import Carditem from './Carditem';
// import Cart from './Cart';


const Single_product = () => {


  const location = useLocation();
  let id = location.state.id;

  const [data, setdata] = useState({});
  useEffect(() => {
    getproduct();
  }, [])

