    let response = await fetch(`http://localhost:8080/api/product/${id}`);
    let res = await response.json();
    setdata(res.message); 
  }



  return (
    <>
  <button style={{marginTop:"150px"}} type="button"  className="btn btn-demo" data-toggle="modal" data-target="#myModal2">
    Right Sidebar Modal
