import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
const Profile = () => {
    const navigate = useNavigate();
    const [data, setdata] = useState([]);

    useEffect(() => {
        getroom();
    }, [])

    const getroom = async () => {
        let response = await fetch('http://localhost:8080/api/room');
        let res = await response.json();
        setdata(res.message);
        console.log(res)
    }

    const booknow = (id) => {
        navigate('/payment', { state: { id: id } })
    }
    return (
        <div className='container'>
            {data && data.map(async (value, index) => {

                    let response = await fetch(`http://localhost:8080/api/rating/${value._id}`);
                    let res = await response.json();
                    let all_rating = res.message;
                    let total_sum = await all_rating.reduce((sum,item)=>{
                        return sum = sum+item;
                    },0)
                    let total_rating = await sum/all_rating.length;
                    console.log('total_rating', total_rating)


                let obj = JSON.parse(value.facility);
                const description = value.description.split(' ').slice(0, 15).join(' ');
                if (value.status == 'available') {

                    return (
                        <div className="card2 mb-3 mt-5" style={{ maxWidth: "740px" }}>
                            <div className="row no-gutters">
                                <div className="col-md-12">
                                    <img src={value.image} className="card-img" alt="..." />
                                </div>
                                <div className="col-md-12">
                                    <div className="card-body">
                                        <h5 className="card-title">{value.title}</h5>
                                        <p className="card-text">{description}... <a href="" onClick={() => booknow(value._id)} >Read More</a></p>

                                        <p className="card-text" style={{ fontWeight: "bold" }}> &nbsp; &nbsp; &nbsp; &nbsp; Price: {value.price} Per Night</p>
                                        <button className="btn btn-info float-right" onClick={() => booknow(value._id)}>Book Now</button><br /><br />
                                        <p className='float-right'>
                                            <div class="star-rating2" key={index}>
                                                <input type="radio" disabled id="5-stars2" name={index} value="5" />
                                                <label for="5-stars2" class="star">&#9733;</label>
                                                <input type="radio" checked disabled id="4-stars2" name={index} value="4" />
                                                <label for="4-stars2" class="star">&#9733;</label>
                                                <input type="radio" disabled id="3-stars2" name={index} value="3" />
                                                <label for="3-stars2" class="star">&#9733;</label>
                                                <input type="radio" disabled id="2-stars2" name={index} value="2" />
                                                <label for="2-stars2" class="star">&#9733;</label>
                                                <input type="radio" disabled id="1-star2" name={index} value="1" />
                                                <label for="1-star2" class="star">&#9733;</label>
                                            </div>
                                        </p><br />

                                        <p className="card-text" style={{ fontWeight: "bold" }}>Room Facilities</p>
                                        <ul className='wrap_list'>
                                            {obj.map((fact) => {
                                                return <li className='facility'>{fact.label}</li>
                                            })}
                                        </ul>


                                    </div>
                                </div>
                            </div>
                        </div>
                    )
                }
            })}
        </div>
    )
}

export default Profile
